import React, { Component } from 'react';
import CarMakesList from './CarMakesList'
import ModelsList from './ModelsList'
import Search from './Search'

const makesURL = `http://localhost:8080/api/makes`
const modelsURL = `http://localhost:8080/api/models`

class MainContainer extends Component {

    state = {
      error: null,
      makes: [],
      models: [],
      isLoading: true,
      inputText: ''
    }

    componentDidMount() {
      this.getMakes()
      this.getModels()
    }

    getMakes = () => {
      fetch(baseURL)
      .then(res => res.json())
      .then(data => this.setState({
        makes: data,
        isLoading: false
      }),
      (error) => {
        this.setState({
          isLoading: false,
          error
        });
      }
    )
  }

    getModels = () => {
      fetch(baseURL)
      .then(res => res.json())
      .then(data => this.setState({
        models: data,
        isLoading: false
      }),
      (error) => {
        this.setState({
          isLoading: false,
          error
        });
      }
    )
  }

  handleInputChange = (e) => {
    this.setState({
      inputText: e.target.value
    })
  }

  filteredMakes = () => this.state.makes.filter(make => {
      return make.toLowerCase().includes(this.state.inputText.toLowerCase())
    })

  render() {
    const { error, isLoading, inputText } = this.state
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (isLoading) {
      return <div>Loading...</div>
    } else {
      return (
        <div>
          <form className="car-form">
          <Search handleInputChange={this.handleInputChange} inputText={inputText}/>
          <CarMakesList makes={this.filteredMakes()}/>
          </form>
        </div>
      );
    }
  }
}

export default MainContainer;
