import React, { Component } from 'react';

class Search extends Component {

  handleChange = (e) => {
    this.props.filterByInput(e.target.value)
  }

  render() {
    return (
      <div>
        <input onChange={this.handleChange} value={this.props.makes} name="input" type="text" className="search" placeholder="search your car..." />
      </div>
    );
  }

}

export default Search;
