import React, { Component } from 'react';
import CarMakesList from './CarMakesList'
import Search from './Search'

const baseURL = `http://localhost:8080/api/makes`

class MainContainer extends Component {

    state = {
      error: null,
      makes: [],
      isLoading: true,
    }

    componentDidMount() {
      this.getCars()
    }

    getCars = () => {
      fetch(baseURL)
      .then(res => res.json())
      .then(data => this.setState({
        makes: data,
        isLoading: false
      }),
      (error) => {
        this.setState({
          isLoading: false,
          error
        });
      }
    )
  }

  filterByInput = (searchInput) => {
    if (searchInput === '') {
      this.setState({
        makes: this.state.allMakes
      })
    }
    else {
      let filteredMakes = this.state.makes.filter(make => {
        return make.toLowerCase().includes(searchInput.toLowerCase())
      })
      this.setState({
        makes: filteredMakes
      })
    }
  }

  render() {
    const { error, isLoading, makes } = this.state
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (isLoading) {
      return <div>Loading...</div>
    } else {
      return (
        <div>
        <form className="car-form">
        <Search filterByInput={this.filterByInput}/>
        <CarMakesList makes={makes}/>
        </form>
        </div>
      );
    }
  }
}

export default MainContainer;
