import React, { Component } from 'react';
import CarMakesList from './CarMakesList'
import Search from './Search'

const baseURL = `http://localhost:8080/api/makes`

class MainContainer extends Component {

    state = {
      error: null,
      makes: [],
      isLoading: true,
      input: ''
    }

    componentDidMount() {
      this.getCars()
    }

    getCars = () => {
      fetch(baseURL)
      .then(res => res.json())
      .then(data => this.setState({
        makes: data,
        isLoading: false
      }),
      (error) => {
        this.setState({
          isLoading: false,
          error
        });
      }
    )
  }

  handleChange = (e)=> {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  filterByInput = (userInput) => {

  }

  render() {
    const { error, isLoading, makes } = this.state
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (isLoading) {
      return <div>Loading...</div>
    } else {
      return (
        <div>
        <form className="car-form">
        <Search handleChange={this.handleChange}/>
        <CarMakesList makes={makes}/>
        </form>
        </div>
      );
    }
  }
}

export default MainContainer;
