import React, { Component } from 'react';
import Vehicle from './Vehicle'

class VehiclesList extends Component {

    state = {
      vehicles: []
    }

    componentDidMount() {
      this.getVehicles()
    }

    getVehicles = () => {
      fetch(`http://localhost:8080/api/vehicles?make=${this.props.make}&model=${this.props.model}`)
      .then(res => res.json())
      .then(data => this.setState({
        vehicles: data
      }))
    }

    filteredVehicles = () => this.state.vehicles.filter(model => {
        return model.toLowerCase().includes(this.props.inputText.toLowerCase())
      })

    render() {
      const models = this.filteredModels()
      return (
        <div>
        List of Models
          <ul>
          {
            models.map((model, id) => {
              return <CarModel key={id} model={model} />
            })
          }
          </ul>
        </div>
      );
    }

  }

export default VehiclesList;
