import React, { Component } from 'react';
import CarMakesList from './CarMakesList'

const baseURL = `http://localhost:8080/api/makes`

class MainContainer extends Component {

    state = {
      error: null,
      makes: [],
      isLoading: true
    }

    componentDidMount() {
      this.getCars()
    }

    getCars = () => {
      fetch(baseURL)
      .then(res => res.json())
      .then(data => this.setState({
        makes: data,
        isLoading: false
      }),
      (error) => {
        this.setState({
          isLoading: false,
          error
        });
      }
    )
  }


    componentDidMount() {
  fetch("https://api.example.com/items")
    .then(res => res.json())
    .then(
      (result) => this.setState({
          isLoaded: true,
          items: result.items
        }),
      // Note: it's important to handle errors here
      // instead of a catch() block so that we don't swallow
      // exceptions from actual bugs in components.
      (error) => {
        this.setState({
          isLoaded: true,
          error
        });
      }
    )
}

  render() {
    const { isLoading, makes } = this.state
    if (isLoading) {
      return <div>Loading...</div>
    }
    return (
      <div>
        <form className="car-form">

          <input type="text" className="search" placeholder="search your car..." />
          <CarMakesList makes={makes}/>
        </form>
      </div>
    );
  }

}

export default MainContainer;
