import React, { Component } from 'react';
import Vehicle from './Vehicle';

class VehiclesList extends Component {


    state = {
      error: null,
      vehicles: []
    }

    componentDidMount() {
      this.getVehicles()
    }

    getVehicles = () => {
      const make = this.props.location.pathname.split('/')[1];
      const model = this.props.location.pathname.split('/')[2];
      fetch(`http://localhost:8080/api/vehicles?make=${make}&model=${model}`)
      .then(res => res.json())
      .then(data => this.setState({
        vehicles: data
      }),
      (error) => {
        this.setState({
          error
        });
      }
     )
    }

    render() {
      console.log(this.props.location.pathname);
      const { vehicles, error } = this.state
      if (error) {
        throw error;
      } else if (vehicles.length === 0) {
        return <NotAvailable text={`No Vehicles Available for ${model}`} />
      }
      return (
        <>
          <h3>List of Vehicles</h3>
        <div className="vehicle-container">
          {
            vehicles.map((vehicle, id) => {
              return <Vehicle key={id} vehicle={vehicle} />
            })
          }
        </div>
        </>
      );
    }

  }

export default VehiclesList;
