import React, { Component } from 'react';
import CarModel from './CarModel';

const baseURL = `http://localhost:8080/api/models?make={make}`

class ModelsList extends Component {

  state = {
    models: []
  }

  componentDidMount() {
    this.getModels()
  }

  getModels = () => {
    fetch(baseURL)
    .then(res => res.json())
    .then(data => this.setState({
      models: data
    }))
  }

  render() {
    const { models } = this.state
    return (
      <div>
      {
        models.map((model, id) => {
          return <CarModel key={id} model={model} />
        })
      }
      </div>
    );
  }

}

export default ModelsList;
