import React, { Component } from 'react';
import './App.css';
import CarMakesList from './CarMakesList'

const baseURL = `http://localhost:8080/api/makes`
class App extends Component {

  state = {
    makes: [],
    isLoading: true
  }

  componentDidMount() {
    this.getCars()
  }


  getCars = () => {
    fetch(baseURL)
    .then(res => res.json())
    .then(data => this.setState({
      makes: data,
      isLoading: false
    }))
  }

  render() {
    const { isLoading, makes } = this.state
    if (isLoading) {
      return <div>Loading...</div>
    }
    return (
      <div>
        <form className="search-form">
          <input type="text" className="search" placeholder="search your car..." />
          <ul>
            <li>Filter for a city</li>
            <li>or a state</li>
          </ul>
        </form>
        <CarMakesList />
      </div>
    );
  }

}

export default App;
