import React, { Component } from 'react';
import CarMakesList from './CarMakesList'

const baseURL = `http://localhost:8080/api/makes`

class MainContainer extends Component {

    state = {
      error: null,
      makes: [],
      isLoading: true
    }

    componentDidMount() {
      this.getCars()
    }

    getCars = () => {
      fetch(baseURL)
      .then(res => res.json())
      .then(data => this.setState({
        makes: data,
        isLoading: false
      }),
      (error) => {
        this.setState({
          isLoading: false,
          error
        });
      }
    )
  }

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    })
    console.log(this.state.makes);
  }

  render() {

    const { error, isLoading, makes } = this.state
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (isLoading) {
      return <div>Loading...</div>
    } else {
      return (
        <div>
        <form className="car-form">
        <input onChange={this.handleChange} name='makes' type="text" className="search" placeholder="search your car..." />
        <CarMakesList makes={makes}/>
        </form>
        </div>
      );
    }
  }
}

export default MainContainer;
