import React, { Component } from 'react';
import CarMakesList from './CarMakesList'
import Search from './Search'

const baseURL = `http://localhost:8080/api/makes`

class MainContainer extends Component {

    state = {
      error: null,
      makes: [],
      isLoading: true,
      inputText: ''
    }

    componentDidMount() {
      this.getCars()
    }

    getCars = () => {
      fetch(baseURL)
      .then(res => res.json())
      .then(data => this.setState({
        makes: data,
        isLoading: false
      }),
      (error) => {
        this.setState({
          isLoading: false,
          error
        });
      }
    )
  }

  handleInputChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  filteredMakes = () => this.state.makes.filter(make => {
      return make.toLowerCase().includes(this.state.inputText.toLowerCase())
    })

  render() {
    const { error, isLoading, makes, inputText } = this.state
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (isLoading) {
      return <div>Loading...</div>
    } else {
      return (
        <div>
        <form className="car-form">
        <Search handleInputChange={this.handleInputChange} inputText={inputText}/>
        <CarMakesList makes={this.filteredMakes()}/>
        </form>
        </div>
      );
    }
  }
}

export default MainContainer;
