    state = {
      error: null,
      makes: [],
      isLoading: true,
      inputText: ''
    }

    componentDidMount() {
      this.getCars()
    }

    getCars = () => {
      fetch(baseURL)
      .then(res => res.json())
      .then(data => this.setState({
        makes: data,
        isLoading: false
