import React, { Component } from 'react';
import CarMakesList from './CarMakesList'
import ModelsList from './ModelsList'
import VehiclesList from './VehiclesList'
import Search from './Search'

const baseURL = `http://localhost:8080/api/makes`

class MainContainer extends Component {

    state = {
      step: 1,
      error: null,
      makes: [],
      make: '',
      model: '',
      isLoading: true,
      inputText: ''
    }

    componentDidMount() {
      this.getMakes()
    }

    getMakes = () => {
      fetch(baseURL)
      .then(res => res.json())
      .then(data => this.setState({
        makes: data,
        isLoading: false
      }),
      (error) => {
        this.setState({
          isLoading: false,
          error
        });
      }
    )
  }

  nextStep = (make, model) => {
    console.log(make);
    const { step } = this.state;
    this.setState({
      step: step + 1,
      make: make,
      model: model,
      inputText: ''
    });
  };

  handleInputChange = (e) => {
    this.setState({
      inputText: e.target.value
    })
  }

  filteredMakes = () => this.state.makes.filter(make => {
      return make.toLowerCase().includes(this.state.inputText.toLowerCase())
    })

  render() {
    console.log(this.state);
    const { error, isLoading, inputText, step, make, model } = this.state
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (isLoading) {
      return <div>Loading...</div>
    } else {

      switch (step) {
        case 1:
        return (
          <form className="car-form">
          <Search handleInputChange={this.handleInputChange} inputText={inputText}/>
          <CarMakesList
            nextStep={this.nextStep}
            handleChange={this.handleChange}
            makes={this.filteredMakes()}
          />
          </form>
      );
        case 2:
        return (
          <form className="car-form">
          <Search handleInputChange={this.handleInputChange} inputText={inputText}/>
          <ModelsList
            nextStep={this.nextStep}
            handleChange={this.handleChange}
            inputText={inputText}
            make={make}
            model={model}
          />
          </form>
        );
        case 3:
        return (
          <form className="car-form">
          <Search handleInputChange={this.handleInputChange} inputText={inputText}/>
          <VehiclesList
            inputText={inputText}
            nextStep={this.nextStep}
            handleChange={this.handleChange}
            make={make}
            model={model}
          />
          </form>
        );
      }
    }
  }
}

export default MainContainer;
